import {
  View,
  Text,
  SafeAreaView,
  Image,
  useWindowDimensions,
  KeyboardAvoidingView,
  ScrollView,
} from "react-native";
import React from "react";
import TextField from "../components/TextField";
import styles from "./styles";
import logo from "../assets/logo.png";
import CustomeBtn from "../components/CustomeBtn";
import SecondaryBtn from "../components/SecondaryBtn";
//firebase
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";
import { addDoc, collection } from "firebase/firestore";

export default function Signup({ navigation }) {
  // const [FirstName, setFirstName] = useState("")
  // const [LastName, setLastName] = useState("")
  // const [Email, setEmail] = useState("")
  // const [MobileNumber, setMobileNumber] = useState("")
  // const [Password, setPassword] = useState("")
  // const [ConfirmPassword, setConfirmPassword] = useState("")

  const { height, width } = useWindowDimensions();

  const signup = () => {
    createUserWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        // Signed in
        const userD = userCredential.user;
        // ...
        console.log("registered succesfully")
 

        const userRef =collection(db,"users")
        const user ={
            FirstName : FirstName,
            LastName: LastName,
            Email: Email,
            MobileNumber: MobileNumber,
            Password: Password,
            ConfirmPassword: ConfirmPassword,
        }
        addDoc(userRef,user).then(()=>{
            navigation.navigate("Home");
        })
      })
      .catch((error) => {
        const errorCode = error.code;
        const errorMessage = error.message;
        // ..
        console.log("error loggin");
      });

    // navigation.navigate('Login')
  };

  function login() {
    navigation.navigate("Login");
  }
  return (
    <SafeAreaView style={styles.container}>
      <ScrollView>
        <KeyboardAvoidingView style={{ width: width, alignItems: "center" }}>
          <View
            style={[
              styles.imgContainer,
              { marginTop: height * 0.1, marginBottom: height * 0.05 },
            ]}
          >
            <Image
              source={logo}
              resizeMode="cover"
              style={{ width: "44.5%", height: height * 0.2 }}
            />
          </View>

          <TextField icon="md-person-sharp" placeholder="Firstname" />
          <TextField icon="md-person-sharp" placeholder="Lastname" />
          <TextField icon="md-mail" placeholder="Email" />
          <TextField
            icon="md-phone-portrait-outline"
            placeholder="Mobile Number"
          />
          <TextField icon="md-lock-closed" placeholder="Create password" />
          <TextField icon="md-lock-closed" placeholder="Confirm password" />
          <View style={{ marginTop: "7%" }} />
          <CustomeBtn name="Sign up" />
          <View style={{ marginTop: "5%" }} />
          <SecondaryBtn name="Already have an account?" onPress={login} />
        </KeyboardAvoidingView>
      </ScrollView>
    </SafeAreaView>
  );
}
